import React, { useState, useEffect } from 'react';

const USERS_URL = 'https://example.com/api/users';

export default function Table() {
  const [users, setUsers] = useState([]);
  const [currentPage, setCurrentPage] = useState(0);
  const [totalPages, setTotalPages] = useState(0);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    fetchData(currentPage);
  }, [currentPage]);

  const fetchData = async (page) => {
    try {
      setIsLoading(true);
      const response = await fetch(`${USERS_URL}?page=${page}`);
      const data = await response.json();
      setUsers(data.results);
      setTotalPages(Math.ceil(data.count / 10));
      setIsLoading(false);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const handleFirstPageClick = () => {
    if (currentPage !== 0) {
      setCurrentPage(0);
    }
  };

  const handlePreviousPageClick = () => {
    if (currentPage !== 0) {
      setCurrentPage(currentPage - 1);
    }
  };

  const handleNextPageClick = () => {
    if (currentPage !== totalPages - 1) {
      setCurrentPage(currentPage + 1);
    }
  };

  const handleLastPageClick = () => {
    if (currentPage !== totalPages - 1) {
      setCurrentPage(totalPages - 1);
    }
  };

  return (
    <div>
      <table className="table">
        <thead>
          <tr>
            <th>ID</th>
            <th>First Name</th>
            <th>Last Name</th>
          </tr>
        </thead>
        <tbody>
          {isLoading ? (
            <tr>
              <td colSpan="3">Loading...</td>
            </tr>
          ) : (
            users.map(user => (
              <tr key={user.id}>
                <td>{user.id}</td>
                <td>{user.firstName}</td>
                <td>{user.lastName}</td>
              </tr>
            ))
          )}
        </tbody>
      </table>
      <section className="pagination">
        <button className="first-page-btn" onClick={handleFirstPageClick} disabled={isLoading || currentPage === 0}>first</button>
        <button className="previous-page-btn" onClick={handlePreviousPageClick} disabled={isLoading || currentPage === 0}>previous</button>
        <button className="next-page-btn" onClick={handleNextPageClick} disabled={isLoading || currentPage === totalPages - 1}>next</button>
        <button className="last-page-btn" onClick={handleLastPageClick} disabled={isLoading || currentPage === totalPages - 1}>last</button>
      </section>
    </div>
  );
}
